package notice;

import java.sql.Date;

/*
 이름          널?       유형             
----------- -------- -------------- 
NUM         NOT NULL NUMBER(10)     
TITLE                VARCHAR2(100)  
CONTENT              VARCHAR2(3000) 
COUNT                NUMBER(10)     
ID                   VARCHAR2(50)   
DATECREATED          DATE 
 */
public class NoticeDTO {
    private int num;
    private String title;
    private String content;
    private int count;
    private String id;
    private String dateCreated;
    
    public NoticeDTO() {
        // TODO Auto-generated constructor stub
    }

    public NoticeDTO(int num, String title, String content, int count, String id, String dateCreated) {
        super();
        this.num = num;
        this.title = title;
        this.content = content;
        this.count = count;
        this.id = id;
        this.dateCreated = dateCreated;
    }

    public int getNum() {
        return num;
    }

    public void setNum(int num) {
        this.num = num;
    }

    public String getTitle() {
        return title;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public String getContent() {
        return content;
    }

    public void setContent(String content) {
        this.content = content;
    }

    public int getCount() {
        return count;
    }

    public void setCount(int count) {
        this.count = count;
    }

    public String getId() {
        return id;
    }

    public void setId(String id) {
        this.id = id;
    }

    public String getDateCreated() {
        return dateCreated;
    }

    public void setDateCreated(String dateCreated) {
        this.dateCreated = dateCreated;
    }
    
    @Override
    public String toString() {
        // TODO Auto-generated method stub
        return "NoticeDTO [num=" + num + ", title=" + title + ", content=" + content
                + ", count=" + count + ", id=" + id + ", dateCreated=" + dateCreated + "]";
    }
}

